INSERT accommodation (id, name, location, price, review_nums, rating, interior, amenities, guest, main_image, vice_images, introduction)VALUES (1, 'Secluded Magical Rainforest Retreat', 'Main Arm', '170', 346, 4.98, 'Entire cabin,2 guests,1 bedroom,1 bed', 'Kitchen, Free parking on premises, Pay TV', 3, 'https://a0.muscache.com/im/pictures/eee320dd-8eeb-4194-8027-85cdb9492065.jpg?aki_policy=large', 'https://a0.muscache.com/im/pictures/17183488/20d13c79_original.jpg?aki_policy=large,https://a0.muscache.com/im/pictures/9efac6ef-3372-4fd5-97ee-43885d574732.jpg?aki_policy=large,https://a0.muscache.com/im/pictures/1b59b175-8356-4282-ac99-ef094289ae47.jpg?aki_policy=large,https://a0.muscache.com/im/pictures/ed58794e-10c1-4966-bab9-573782fa6f97.jpg?aki_policy=large', 'Cross the bridge and enter a magical paradise. Set amongst tree tops perched in a tropical oasis is this romantic and secluded cabin overlooking the creek. Beautifully decorated interior with a Balinese feel, Complete with fully equipped kitchen, outdoor breakfast bar, wifi, Netflix, cozy wood fire, Escape to this magical paradise. We are only 7km from the thriving town of Mullumbimby full of character and great village shopping. The restaurants and eateries are amazing with so many choices from Japanese to Thai to good old fashioned pub meals.');
