INSERT accommodation (id, name, location, price, review_nums, rating, interior, amenities, guest, main_image, vice_images, introduction)VALUES (7, 'Nearest neighbour is World Heritage', 'Cambridge Plateau', '95', 430, 4.95, 'Entire cabin,2 guests,Studio,1 bed', 'Indoor fireplace, Heating, Hot water', 4, 'https://a0.muscache.com/im/pictures/84241964/827d61dd_original.jpg?aki_policy=large', 'https://a0.muscache.com/im/pictures/93f33caf-c753-4979-99a4-91073c0df255.jpg?aki_policy=large,https://a0.muscache.com/im/pictures/17183196/d719cbae_original.jpg?aki_policy=large,https://a0.muscache.com/im/pictures/72036962/0429a484_original.jpg?aki_policy=large,https://a0.muscache.com/im/pictures/92241438/c09b576f_original.jpg?aki_policy=large', 'Remote and 15 metres off world heritage listed rainforest. This is the ultimate if you are looking for a place to wind down and simply enjoy watching the day go by & recharge your entire self in this beautiful part of the world. As we  are on a property that is basically surrounded by National Park are closet neighbours are our magnificent wedge tail eagles, wallabies and all the other wildlife that call here home,');
