INSERT accommodation (id, name, location, price, review_nums, rating, interior, amenities, guest, main_image, vice_images, introduction)VALUES (41, 'Suffolks Romantic Studio Cottage', 'Suffolk Park', '126', 294, 4.95, 'Entire guesthouse,2 guests,1 bedroom,1 bed', 'Kitchen, Free parking on premises, Laptop friendly workspace', 5, 'https://a0.muscache.com/im/pictures/c127b315-0f51-4a20-84be-eabfb8b03ede.jpg?aki_policy=large', 'https://a0.muscache.com/im/pictures/72037104/ed99b924_original.jpg?aki_policy=large,https://a0.muscache.com/im/pictures/a8502b3a-4163-46db-9e39-1449fc8fc15b.jpg?aki_policy=large,https://a0.muscache.com/im/pictures/35b0f32c-adff-4370-91b9-2ca0a95434d4.jpg?aki_policy=large,https://a0.muscache.com/im/pictures/17183268/795c1f23_original.jpg?aki_policy=large', 'This cute cottage is the perfect place for your romantic Byron getaway. Beautiful high ceilings with French doors overlooking our beautiful tree filled garden. Located in Suffolk Park about 7 mins drive from downtown Byron. Not suitable for children. After a day exploring the area or at the beach the studio is a great relaxing place to come back to. Suffolk Park has which is around the corner has a small supermarket, bakery, bottle shop, pub and a couple of restaurants. We are also very close to Byron at Byron which is a great place to sit by the pool for a cocktail or a meal. The golf course not far away either. Broken Head famous for its surf 5 away.');
